import React, {Component} from 'react';

class Answer extends Component {

    constructor(props){
        super(props);
        this.state = {
            question : ""
        }
    }

    // handlePostChange = (e) => {
    //     this.setState({question:e.target.value})
    //     // console.log(this.props)
    // }

    upvote = () => {
        this.props.upvote(this.props.questionID, this.props.ansId);
    }

    render() {
        // console.log(this.props)
    	return(
            <div className = "message">
                <div className="mdl-card mdl-cell mdl-cell--12-col">
                    <div className="mdl-card__supporting-text mdl-grid mdl-grid--no-spacing">
                        <div className = "answer">
                            <div className="section__text mdl-cell mdl-cell--10-col-desktop mdl-cell--6-col-tablet mdl-cell--3-col-phone">
                                {this.props.ans[0]}
                            </div>
                        </div>
                        <div className = "answer">
                            {this.props.ans[1].toNumber()}
                        </div>
                        <div className = "upvote">
                            <button onClick={this.upvote}><i className="fas fa-thumbs-up"></i><span>Upvote</span></button>
                        </div>
                    </div>
                </div>
            </div>)
    }
}

export default Answer;
