import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
// import { answerItContract } from "./setup";
import Header from './header';
import Posts from "./posts";
import TruffleContract from 'truffle-contract'
import PostQuestion from "./postquestion";
import Web3 from 'web3';
import AnswerIT from '../../backend/build/contracts/AnswerIT.json'; 

class App extends Component {
    constructor(props){
        super(props)
        this.state = {
            contracts : {},
            account : '0x0',
            questions : [],
            questionCount : 0
        }
        // this.handleVoting=this.handleVoting.bind(this)
        // let web3 = window.web3;  

        // this.web3Provider = web3.currentProvider;
        // this.web3 = new Web3(web3.currentProvider);

        if (typeof window.web3 === 'undefined') {
            this.web3Provider = new Web3.providers.HttpProvider('http://127.0.0.1:7545');
        } else {
            var myWeb3 = new Web3(window.web3.currentProvider); 
            myWeb3.eth.defaultAccount = window.web3.eth.defaultAccount;

           console.log("defaultAccount : ",  myWeb3.eth.defaultAccount);
           this.web3Provider = window.web3.currentProvider;
        }

    // this.web3Provider = new Web3.providers.HttpProvider('http://127.0.0.1:7545')
        this.web3 = new Web3(this.web3Provider)
        this.AnswerIT = TruffleContract(AnswerIT)
        this.AnswerIT.setProvider(this.web3Provider)


    }

    postAnswer = (id, answer) => {
        console.log(id, answer);
        this.contracts.PostAnswer(id, answer, { from: this.state.account, gas:3000000 })
        this.getQuestions();
    }

    postQuestion = (question) => {
        console.log(question, this.state.account)
        this.contracts.PostQuestion(question,  { from: this.state.account, gas:3000000 });
        this.getQuestions();
        // console.log(this.contracts.msgCount().resolve)
        // console.log(this.contracts.Messages(7),">>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>>")
    }

    getQuestions = () => {
        var self = this;
        self.state.contracts.msgCount().then(function(msgCount){
            console.log(msgCount.toNumber())

            var questions = [];

            for (var i = 0; i < msgCount.toNumber(); i++) {
                console.log("here")
                self.state.contracts.Messages(i).then(function(q){
                    var numAnswers = q[11].toNumber();
                    var id = q[1].toNumber();
                    var allAnswers = [];
                    console.log(numAnswers, "----------======")
                    if(numAnswers == 0) {
                        q.push(allAnswers)
                        questions.push(q);
                        self.setState({questions:questions})

                    } else {
                        for(let j = 0; j < numAnswers; j++) {
                            var answers = [];
                            var upvoteCounts = [];
                            var upvoteValues = [];

                            self.state.contracts.IterateAnswers(id, j).then(function(answer){
                                answers.push(answer);
                                self.state.contracts.GetUpvoteCount(id,j).then(function(upvoteCount) {
                                    upvoteCounts.push(upvoteCount);
                                    self.state.contracts.GetUpvoteValue(id,j).then(function(upvoteValue){
                                        upvoteValues.push(upvoteValue);
                                        if(j == numAnswers - 1)
                                            this.setAnswers(answers, upvoteCounts, upvoteValues, numAnswers, q, questions)
                                    })
                                })
                            })
                        }
                    }

                })
            }
        })


    }

    setAnswers = (answers, upvoteCounts, upvoteValues, numAnswers, q, questions) => {
        var allAnswers = [];
        for(let x = 0; x < numAnswers; x++) {
            console.log("ghjk")
            allAnswers.push([answers[x], upvoteCounts[x], upvoteValues[x]])
        }

            q.push(allAnswers);
            questions.push(q);
            self.setState({questions:questions})

    }

    componentDidMount = () => {
        this.web3.eth.getCoinbase((err, acc)=>{
            console.log("acc", acc);
            this.setState({account : acc});
            this.AnswerIT.deployed().then((instance) => {
                this.contracts = instance;
                this.setState({contracts : this.contracts}, function(){
                    this.getQuestions();
                })
                console.log("App Loaded:",this.contracts);
                console.log("App From state:",this.state.contracts);
                console.log("Logged in addr: " , this.state.account);
            })
        });
    }



    render = () => {
        console.log(this.state.questions)
        var self = this;
        return (
            <div className="App">
                <Header />
                <div className="posts">
                    <PostQuestion question={this.postQuestion}/>
                    <div className="questions">
                    { this.state.questions.map(function(item, i){
                        return <Posts key={i} question={item[2]} answer={item[12]} id= {item[1].toNumber()} postAnswer={self.postAnswer}/>
                        })
                    }
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
